import React from "react";

function CreateArea() {
  return (
    <div>
      <form>
        <input  className="note" name="title" placeholder="Title" />
        <textarea  className="note" name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
